function getViewportWidth(){return window.innerWidth?window.innerWidth:document.body&&document.body.offsetWidth?document.body.offsetWidth:0}function getViewportHeight(){return window.innerHeight?window.innerHeight:document.body&&document.body.offsetHeight?document.body.offsetHeight:0}function attachEventListener(e,t,n){if(e.addEventListener)e.addEventListener(t,n,!1);else if(e.attachEvent){t="on"+t;e.attachEvent(t,n)}else e["on"+t]=n}function detachEventListener(e,t,n){if(e.removeEventListener)e.removeEventListener(t,n,!1);else if(e.detachEvent){t="on"+t;e.detachEvent(t,n)}else e["on"+t]=null}function cancelEvent(e){e.preventDefault?e.preventDefault():e.returnValue=!1}function cancelPropogation(e){e.stopPropagation?e.stopPropagation():e.cancelBubble=!0}function gaEvent(e,t,n,r,i){if(e===null)throw new Error("gaEvent: Category is required.");if(t===null)throw new Error("gaEvent: Action is required.");n=n||null;r=r||null;i=i||!1;r===null&&i===!1?_gaq.push(["_trackEvent",e,t,n]):_gaq.push(["_trackEvent",e,t,n,r,i]);console.log("gaEvent: category: "+e+" action: "+t+" label: "+n+" value: "+r+" nonInteract: "+i)};